
// Removed the problematic vite/client reference to fix "Cannot find type definition file" error

// Declare image modules so TypeScript recognizes them
declare module '*.png' {
  const value: string;
  export default value;
}

declare module '*.jpg' {
  const value: string;
  export default value;
}

declare module '*.jpeg' {
  const value: string;
  export default value;
}

declare module '*.svg' {
  const value: string;
  export default value;
}
